import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './login.css';

const Login = () => {
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setLoginData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch(`http://localhost:3001/users?username=${loginData.username}&password=${loginData.password}`);
      const users = await res.json();
      if (users.length > 0) {
        alert('Login successful!');
        navigate('/home');
      } else {
        alert('Invalid credentials');
      }
    } catch (err) {
      alert('Error connecting to server');
    }
  };

  return (
    <>
      <header><div className="logo"><h1><abbr title="Dua Music Player">DMP</abbr></h1></div></header>
      <main>
        <div className="login-container">
          <h2>Login to DMP</h2>
          <form onSubmit={handleSubmit}>
            <input type="text" name="username" placeholder="Enter Username" value={loginData.username} onChange={handleChange} required />
            <input type="password" name="password" placeholder="Enter Password" value={loginData.password} onChange={handleChange} required />
            <button type="submit" id="login-btn">Login</button>
          </form>
          <div className="login-options">
            <p>Don't have an account? <a href="/createAcc.jsx">Create one</a></p>
          </div>
        </div>
      </main>
      <footer><p>&copy; 2025 DMP - Dua Music Player</p></footer>
    </>
  );
};

export default Login;
