import React from 'react';
import './login.css';

const Login = () => {
  const handleSubmit = (e) => {
    e.preventDefault();
    const username = e.target.username.value.trim();
    const password = e.target.password.value;

    if (!username || !password) {
      alert('Username and Password are required');
      return;
    }

    // You can place your login logic here
    alert(`Login attempted for ${username}`);
  };

  return (
    <>
      <header>
        <div className="logo">
          <h1><abbr title="Dua Music Player">DMP</abbr></h1>
        </div>
      </header>

      <main>
        <div className="login-container">
          <h2>Login to DMP</h2>
          <form id="login-form" onSubmit={handleSubmit}>
            <input type="text" id="username" name="username" placeholder="Enter Username" required />
            <input type="password" id="password" name="password" placeholder="Enter Password" required />
            <button type="submit" id="login-btn">Login</button>
          </form>

          <div className="login-options">
            <p>Don't have an account? <a href="createAcc.html">Create one</a></p>
          </div>
        </div>
      </main>

      <footer>
        <p>&copy; 2025 DMP - Dua Music Player</p>
      </footer>
    </>
  );
};

export default Login;
