import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './createAcc.css';

const CreateAccount = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    username: '',
    pass: '',
    conPass: ''
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const validateForm = () => {
    const { fullName, email, pass, conPass } = formData;
    if (!fullName.trim() || !username || !pass || !conPass) {
      alert('Must be filled');
      return false;
    }
    if (pass !== conPass) {
      alert('Password not SAME');
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      const response = await fetch('http://localhost:3001/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          username: formData.username,
          password: formData.pass
        })
      });
      if (response.ok) {
        alert('Account created!');
        navigate('/login');
      } else {
        alert('Something went wrong');
      }
    } catch (err) {
      alert('Error connecting to server');
    }
  };

  return (
    <>
      <header><div className="logo"><h1><abbr title="Dua Music Player">DMP</abbr></h1></div></header>
      <div className="container">
        <h1 style={{ textAlign: 'center' }}>Create Your Account</h1>
        <form onSubmit={handleSubmit}>
          <p>Full Name <br /><input type="text" name="fullName" placeholder="John Doe" value={formData.fullName} onChange={handleChange} /></p>
          <p>Username <br /><input type="text" name="username" placeholder="john123" value={formData.username} onChange={handleChange} /></p>
          <p>Password <br /><input type="password" name="pass" placeholder="At least 8 characters" value={formData.pass} onChange={handleChange} /></p>
          <p>Confirm Password <br /><input type="password" name="conPass" placeholder="Re-enter your password" value={formData.conPass} onChange={handleChange} /></p>
          <button type="submit" className="btnCreate">Create Account</button>
        </form>
      </div>
      <footer><p>&copy; 2025 DMP - Dua Music Player</p></footer>
    </>
  );
};

export default CreateAccount;
