import React, { useState } from 'react';
import './CreateAccount.css';

const CreateAccount = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    pass: '',
    conPass: '',
  });

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const validateForm = () => {
    const { fullName, email, pass, conPass } = formData;

    if (!fullName.trim() || !email.trim() || !pass || !conPass) {
      alert('Must be filled');
      return false;
    }

    if (pass !== conPass) {
      alert('Password not SAME');
      return false;
    }

    const emailPattern = /^[^]+@[a-z]+\.[a-z]{2,4}$/;
    if (!emailPattern.test(email)) {
      alert('Please enter a valid email address');
      return false;
    }

    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // handle successful form submission here
      alert("Form submitted successfully!");
    }
  };

  return (
    <>
      <header>
        <div className="logo">
          <h1><abbr title="Dua Music Player">DMP</abbr></h1>
        </div>
      </header>
      <div className="container">
        <h1 style={{ textAlign: "center" }}>Create Your Account</h1>
        <form onSubmit={handleSubmit}>
          <p>Full Name <br />
            <input type="text" name="fullName" placeholder="John Doe" value={formData.fullName} onChange={handleChange} />
          </p>
          <p>Email Address <br />
            <input type="email" name="email" placeholder="john@example.com" value={formData.email} onChange={handleChange} />
          </p>
          <p>Password <br />
            <input type="password" name="pass" placeholder="At least 8 characters" value={formData.pass} onChange={handleChange} />
          </p>
          <p>Confirm Password <br />
            <input type="password" name="conPass" placeholder="Re-enter your password" value={formData.conPass} onChange={handleChange} />
          </p>
          <button type="submit" className="btnCreate">Create Account</button>
        </form>
      </div>
      <footer>
        <p>&copy; 2025 DMP - Dua Music Player</p>
      </footer>
    </>
  );
};

export default CreateAccount;
