import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './createAcc.css';

const CreateAccount = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    pass: '',
    conPass: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const validateForm = () => {
    const { fullName, email, pass, conPass } = formData;

    if (!fullName.trim() || !email.trim() || !pass || !conPass) {
      alert('All fields must be filled');
      return false;
    }

    if (pass !== conPass) {
      alert('Passwords do not match');
      return false;
    }

    const emailPattern = /^[^]+@[a-z]+\.[a-z]{2,4}$/;
    if (!emailPattern.test(email)) {
      alert('Please enter a valid email address');
      return false;
    }

    return true;
  };

  const saveUserToLocalDB = () => {
    const existingUsers = JSON.parse(localStorage.getItem('users')) || [];

    const isEmailTaken = existingUsers.some(user => user.email === formData.email);
    if (isEmailTaken) {
      alert('Email is already registered');
      return false;
    }

    const newUser = {
      name: formData.fullName,
      email: formData.email,
      password: formData.pass,
    };

    existingUsers.push(newUser);
    localStorage.setItem('users', JSON.stringify(existingUsers));
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      if (saveUserToLocalDB()) {
        alert('Account created successfully!');
        navigate('/');
      }
    }
  };

  return (
    <>
      <header>
        <div className="logo">
          <h1><abbr title="Dua Music Player">DMP</abbr></h1>
        </div>
      </header>
      <div className="container">
        <h1 style={{ textAlign: "center" }}>Create Your Account</h1>
        <form onSubmit={handleSubmit}>
          <p>Full Name <br />
            <input
              type="text"
              name="fullName"
              placeholder="John Doe"
              value={formData.fullName}
              onChange={handleChange}
            />
          </p>
          <p>Email Address <br />
            <input
              type="email"
              name="email"
              placeholder="john@example.com"
              value={formData.email}
              onChange={handleChange}
            />
          </p>
          <p>Password <br />
            <input
              type="password"
              name="pass"
              placeholder="At least 8 characters"
              value={formData.pass}
              onChange={handleChange}
            />
          </p>
          <p>Confirm Password <br />
            <input
              type="password"
              name="conPass"
              placeholder="Re-enter your password"
              value={formData.conPass}
              onChange={handleChange}
            />
          </p>
          <button type="submit" className="btnCreate">Create Account</button>
        </form>
      </div>
      <footer>
        <p>&copy; 2025 DMP - Dua Music Player</p>
      </footer>
    </>
  );
};

export default CreateAccount;
