import React, { useEffect, useState, useRef } from 'react';
import './songpage.css';

const SongPlayer = () => {
  const [song, setSong] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const youtubeRef = useRef(null);

  useEffect(() => {
    const selectedSong = JSON.parse(localStorage.getItem("selectedSong"));
    setSong(selectedSong);
  }, []);

  const playVideo = () => {
    if (song && youtubeRef.current) {
      youtubeRef.current.src = `https://www.youtube.com/embed/${song.videoId}?autoplay=1&controls=1`;
      setIsPlaying(true);
    }
  };

  const pauseVideo = () => {
    if (song && youtubeRef.current) {
      youtubeRef.current.src = `https://www.youtube.com/embed/${song.videoId}?autoplay=0&controls=1`;
      setIsPlaying(false);
    }
  };

  const goBack = () => {
    window.history.back();
  };

  return (
    <>
      <main>
        <div className="song-container">
          <button className="back-button" onClick={goBack}>&#8592; Back</button>

          <div className="cover-container">
            {song && <img id="cover-image" src={song.thumbnail} alt="Song Cover" />}
          </div>

          <h2 id="song-title">{song ? song.title : "Loading..."}</h2>

          <audio id="audio-player" controls>
            {song && <source id="audio-source" src={song.audioSrc || ""} type="audio/mp3" />}
            Your browser does not support the audio element.
          </audio>

          <div className="video-container">
            {song && (
              <iframe
                ref={youtubeRef}
                id="youtube-player"
                title="YouTube Video Player"
                width="560"
                height="315"
                src={`https://www.youtube.com/embed/${song.videoId}?autoplay=0&controls=1`}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
              ></iframe>
            )}
          </div>

          <div className="controls">
            <button onClick={playVideo}>▶ Play</button>
            <button onClick={pauseVideo}>⏸ Pause</button>
          </div>
        </div>
      </main>
    </>
  );
};

export default SongPlayer;
