import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './songs.css';

const Admin = () => {
  const [songs, setSongs] = useState([]);
  const [form, setForm] = useState({ title: '', videoId: '' });

  useEffect(() => {
    fetchSongs();
  }, []);

  const fetchSongs = async () => {
    const res = await axios.get('http://localhost:5000/songs');
    setSongs(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/songs', form);
    setForm({ title: '', videoId: '' });
    fetchSongs();
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/songs/${id}`);
    fetchSongs();
  };

  return (
    <div className="admin-panel">
      <h2>Admin Panel</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Song Title"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="YouTube Video ID"
          value={form.videoId}
          onChange={(e) => setForm({ ...form, videoId: e.target.value })}
          required
        />
        <button type="submit">Add Song</button>
      </form>

      <ul className="song-list">
        {songs.map((song) => (
          <li key={song._id} className="song-item">
            <img
              src={`https://img.youtube.com/vi/${song.videoId}/default.jpg`}
              alt={song.title}
            />
            <span>{song.title}</span>
            <button onClick={() => handleDelete(song._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Admin;
