import React, { useEffect, useState, useRef } from 'react';
import './songpage.css';

const SongPlayer = () => {
  const [song, setSong] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    const selectedSong = JSON.parse(localStorage.getItem("selectedSong"));
    setSong(selectedSong);
  }, []);

  const playAudio = () => {
    if (audioRef.current) {
      audioRef.current.play();
      setIsPlaying(true);
    }
  };

  const pauseAudio = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      setIsPlaying(false);
    }
  };

  const goBack = () => {
    window.history.back();
  };

  return (
    <>
      <header>
        <div className="logo">
          <h1><abbr title="Dua Music Player">DMP</abbr></h1>
        </div>

        <div className="search-bar">
          <input type="text" placeholder="Search music..." />
          <button type="submit">Search</button>
        </div>
      </header>

      <main>
        <div className="song-container">
          <button className="back-button" onClick={goBack}>&#8592; Back</button>

          <div className="cover-container">
            {song && <img id="cover-image" src={song.thumbnail} alt="Song Cover" />}
          </div>

          <h2 id="song-title">{song ? song.title : "Loading..."}</h2>

          {song?.videoId && (
            <div className="video-container">
              <iframe
                id="youtube-player"
                title="YouTube Video Player"
                width="400"
                height="315"
                src={`https://www.youtube.com/embed/${song.videoId}?autoplay=0&controls=1`}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
              ></iframe>
            </div>
          )}
        </div>
      </main>

      <footer>
        <p>&copy; 2025 DMP - Dua Music Player</p>
      </footer>
    </>
  );
};

export default SongPlayer;
